mapper.update:
UPDATE MEMBER
SET MEMBER_ROLE = #{member_role} 
WHERE MEMBER_ID = #{member_id}

mapper.select:
SELECT MEMBER_NO, MEMBER_ID, MEMBER_PW, MEMBER_NAME, MEMBER_GENDER, MEMBER_EMAIL, MEMBER_ADDR, MEMBER_PHONE, MEMBER_BIRTH, MEMBER_ROLE, MEMBER_DELETEDATE, MEMBER_BLOCKDATE
FROM MEMBER
WHERE MEMBER_ID = #{MEMBER_ID}

SELECT COUNT(member_id)
FROM MEMBER 
WHERE MEMBER_ID = #{member_id}

SELECT COUNT(member_email)
FROM MEMBER
WHERE MEMBER_EMAIL = #{member_email}
