mapper.select:
SELECT CALENDAR_NO, CALENDAR_TITLE, CALENDAR_CONTENT, CALENDAR_MDATE, CALENDAR_REGDATE, MEMBER_ID
FROM CALENDAR
WHERE CALENDAR_NO = #{calendar_no}

mapper.update:
UPDATE CALENDAR
SET CALENDAR_TITLE = #{calendar_title}, CALENDAR_CONTENT = #{calendar_content}
WHERE CALENDAR_NO = #{calendar_no}
