mapper.select:
SELECT QNANO, QNAQWRITER, QNATITLE, QNAQCONTENT, QNAQREGDATE, QNASECRET, QNARESPONSE, QNAAWRITER, QNAACONTENT, QNAAREGDATE
FROM QNA
WHERE QNANO = #{qnano}

mapper.update:
UPDATE QNA
SET QNATITLE = #{qnatitle}, QNAQCONTENT = #{qnaqcontent}
WHERE QNANO = #{qnano}

UPDATE QNA
SET QNAAWRITER = #{qnaawriter}, QNAACONTENT = #{qnaacontent}, QNAAREGDATE = SYSDATE, QNARESPONSE = 'Y'
WHERE QNANO = #{qnano}
