mapper.select:
SELECT REVIEW_NO, REVIEW_ID, REVIEW_CONTENT, REVIEW_CLASSNO, REVIEW_STAR, REVIEW_DATE, REVIEW_UPDATE
FROM REVIEW
WHERE REVIEW_NO = #{review_no}

mapper.update:
UPDATE REVIEW
SET REVIEW_CONTENT = #{review_content}, REVIEW_STAR = #{review_star}, REVIEW_UPDATE = SYSDATE
WHERE REVIEW_NO = #{review_no}
